import React, { Component, useState } from "react";
import paths from "../../config/paths";
import { newReleases } from "./sidebarData";

import * as AiIcons from "react-icons/ai";
import * as RiIcons from "react-icons/ri";
import * as FaIcons from "react-icons/fa";
import { IconContext } from "react-icons";
import Link from "next/link";

import SVGInline from "react-svg-inline";

function Sidebar() {
  const [opened, setOpened] = useState(false);
  const [openedList, setOpenedList] = useState({});

  const renderItemContent = (item) => {
    return (
      <>
        {item.image && (
          <img
            className="sidebar-image"
            src={item.image}
            alt={item.title}
            title={item.title}
          />
        )}
        {item.icon && (
          <div className="SVGInline">
            <span className="mr-3 border-white SVGIcon">{item.icon}</span>
          </div>
        )}
        <span className="sidebar-label">{item.title}</span>
      </>
    );
  };

  const renderItem = () => {
    return (
      <>
        {newReleases.map((item, index) => {
          return (
            <li key={index}>
              <a href={item.path}>
                {renderItemContent(item)}
                {/* <span className="mr-3 border-white SVGIcon">{item.icon}</span>
                <span className="sidebar-label">{item.title}</span> */}
              </a>
            </li>
          );
        })}
      </>
    );
  };

  const renderMenuList = (title, headerPath) => {
    return (
      <div className="sidebar_menu">
        <a href={headerPath} className="sidebar_title">
          {title}
        </a>
        <ul>{renderItem()}</ul>
      </div>
    );
  };

  const renderHome = () => {
    return renderMenuList("Home", paths.index);
  };

  const renderReview = () => {
    return renderMenuList("Review", paths.reviews);
  };

  const renderAllGames = () => {
    return renderMenuList("All Games", paths.games);
  };

  return (
    <IconContext.Provider value={{ className: "react-icons" }}>
      <nav className="container m-10">
        {renderHome()}
        {renderReview()}
        <div className="">
          <span>New Releases</span>
          {/* <ul></ul> */}
        </div>
        <div className="">
          <span>Top</span>
          <ul>
            <li>
              <a href="/">
                <div className="SVGInline">
                  <span className="mr-3 border-white SVGIcon">
                    <AiIcons.AiFillTrophy />
                  </span>
                  <span className="label">Best of the year</span>
                </div>
              </a>
            </li>
            <li>
              <a href="/">
                <div className="SVGInline">
                  <span className="mr-3 border-white SVGIcon">
                    <RiIcons.RiBarChart2Fill />
                  </span>
                  <span className="label">Popular 2019</span>
                </div>
              </a>
            </li>
            <li>
              <a href="/">
                <div className="SVGInline">
                  <span className="mr-3 border-white SVGIcon">
                    <FaIcons.FaCrown />
                  </span>
                  <span className="label">All time top 250</span>
                </div>
              </a>
            </li>
          </ul>
        </div>
        <div className="">
          <a href="/">All Games</a>
        </div>
        <div className="">
          <div className="">
            <a href="/">Platforms</a>
          </div>
          <ul>
            <li>
              <a href="/">
                <div className="SVGInline">
                  <span className="mr-3 border-white SVGIcon">
                    <AiIcons.AiFillWindows />
                  </span>
                  <span className="label">PC</span>
                </div>
              </a>
            </li>
            <li>
              <a href="/">
                <div className="SVGInline">
                  <span className="mr-3 border-white SVGIcon">
                    <FaIcons.FaPlaystation />
                  </span>
                  <span className="label">Playstation</span>
                </div>
              </a>
            </li>
            <li>
              <a href="/">
                <div className="SVGInline">
                  <span className="mr-3 border-white SVGIcon">
                    <FaIcons.FaXbox />
                  </span>
                  <span className="label">Xbox One</span>
                </div>
              </a>
            </li>
          </ul>
        </div>
        <div className="">
          <div className="">
            <a href="/">Genres</a>
          </div>
          <ul>
            <li>
              <a href="/">
                <div className="SVGInline">
                  <span className="mr-3 border-white SVGIcon">
                    <AiIcons.AiFillWindows />
                  </span>
                  <span className="label">Action</span>
                </div>
              </a>
            </li>
            <li>
              <a href="/">
                <div className="SVGInline">
                  <span className="mr-3 border-white SVGIcon">
                    <FaIcons.FaPlaystation />
                  </span>
                  <span className="label">Strategy</span>
                </div>
              </a>
            </li>
            <li>
              <a href="/">
                <div className="SVGInline">
                  <span className="mr-3 border-white SVGIcon">
                    <FaIcons.FaXbox />
                  </span>
                  <span className="label">RPG</span>
                </div>
              </a>
            </li>
          </ul>
        </div>
      </nav>
    </IconContext.Provider>
  );
}

export default Sidebar;
